import React, { useState } from 'react';
import '../styles/components.css';

const TermCard = ({ term }) => {
  const [expanded, setExpanded] = useState(false);

  return (
    <div className={`term-card ${term.difficulty.toLowerCase()}`}>
      <div className="term-header">
        <h3>{term.term}</h3>
        <span className={`difficulty-badge ${term.difficulty.toLowerCase()}`}>
          {term.difficulty}
        </span>
      </div>
      <p className="term-category">{term.category}</p>
      <p className="term-definition">{term.definition}</p>
      
      <button 
        className="expand-button"
        onClick={() => setExpanded(!expanded)}
      >
        {expanded ? 'Show Less' : 'Learn More'}
      </button>
      
      {expanded && (
        <div className="term-details">
          <div className="detail-section">
            <h4>How to Use</h4>
            <p>{term.howToUse}</p>
          </div>
          <div className="detail-section">
            <h4>When to Use</h4>
            <p>{term.whenToUse}</p>
          </div>
          <div className="term-actions">
            <button className="action-button bookmark">Bookmark</button>
            <button className="action-button mastered">Mark as Mastered</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default TermCard;
